        gap: 12px;
        width: 100%;
        max-width: 482px;
        padding: 0;
        display: flex;
    }

    .if-friezes .if-frieze:before {
        content: "";
        display: none;
    }

    .if-friezes .if-frieze__title {
        margin: 0;
        font-family: BrownBold, sans-serif;
        font-size: 18px;
        font-weight: 700;
        line-height: 24.3px;
    }

    .if-friezes .if-frieze__sub-title {
        margin: 0;
        font-family: BrownRegular, sans-serif;
        font-size: 16px;
        font-weight: normal;
        line-height: 24.3px;
    }

    .if-friezes .if-frieze__wrap-title {
        flex-direction: column;
        padding: 20px 8px 20px 0;
        display: flex;
    }

    .if-friezes .if-frieze__index {
        flex-shrink: 0;
        width: 16px;
        height: 16px;
        font-family: BrownBold, sans-serif;
        font-size: 16px;
        font-weight: 700;
        line-height: 24.3px;
        position: absolute;
        transform: translate3d(-27px, -4.1px, 0);
    }

    .if-friezes .if-frieze__text {
        color: #424242;
        font-family: BrownRegular, sans-serif;
        font-size: 16px;
        font-weight: normal;
        line-height: 145%;
    }

    .if-friezes .if-frieze__decorator {
        flex-direction: column;
        align-items: center;
        margin-left: 40px;
        display: flex;
    }

    .if-friezes .if-frieze__decorator div:first-child {
        height: calc(40% - 8px);
    }

    .if-friezes .if-frieze__decorator div:last-child {
        height: calc(60% - 12px);
    }

    .if-friezes .if-frieze__decorator div:nth-child(2) {
        background-color: #fff;
        border: 2px solid #ffb800;
        border-radius: 50%;
        width: 16px;
        height: 16px;
        margin: 2px;
        position: relative;
    }

    .if-friezes .if-frieze__decorator div:first-child, .if-friezes .if-frieze__decorator div:last-child {
        background-color: #ffb800;
        width: 2px;
    }

    .if-friezes .if-frieze:first-child .if-frieze__decorator div:first-child, .if-friezes .if-frieze:last-child .if-frieze__decorator div:last-child {
        background-color: #fff;
    }

    .if-friezes .if-frieze--active .if-frieze__decorator div:nth-child(2) {
        background-color: #ffb800;
    }

    .if-friezes .if-frieze-accordion {
        cursor: pointer;
    }

    .if-friezes .if-frieze-accordion .if-frieze-accordion__text {
        display: none;
    }

    .if-friezes .if-frieze-accordion--open .if-frieze-accordion__text {
        color: #767676;
        margin: 0;
        font-family: BrownRegular, sans-serif;
        font-size: 16px;
        font-weight: normal;
        line-height: 21px;
        display: block;
    }

    .if-friezes .if-frieze-accordion--open .if-frieze-accordion__arrow {
        transform: rotate(180deg);
    }

    .if-friezes .if-frieze-accordion__wrapper {
        width: 100%;
        padding-bottom: 14px;
        padding-right: 14px;
    }

    .if-friezes .if-frieze-accordion__head {
        flex-direction: row;
        justify-content: space-between;
        align-items: end;
        width: 100%;
        display: flex;
    }

    .if-friezes .if-frieze-accordion__arrow {
        margin-bottom: 20px;
    }

    .if-frieze-wrapper {
        background-color: #fafbff;
        max-width: 540px;
        margin-left: auto;
        margin-right: auto;
        padding: 24px;
    }

    .if-frieze-wrapper__title {
        color: #202020;
        margin-bottom: 16px;
        font-family: BrownBold, sans-serif;
        font-size: 24px;
        font-weight: 700;
        line-height: 135%;
    }

    .if-frieze-wrapper__description {
        color: #424242;
        margin-bottom: 40px;
        font-family: BrownRegular, sans-serif;
        font-size: 18px;
        font-weight: normal;
        line-height: 145%;
    }







